function getElementsWithAtt(attributePattern){var matchingElements=[],allElements=document.getElementsByTagName("*");for(var i=0;i<allElements.length;i++){var currentElement=allElements[i],allAttributes=currentElement.attributes;for(var j=0;j<allAttributes.length;j++){var attributeNode=allAttributes[j],attributeString=attributeNode.nodeName,patternLength=attributePattern.length,attributeSubStr=attributeString.substring(0,patternLength);if(attributeSubStr==attributePattern){var attributeDetails=[currentElement,attributeString];matchingElements.push(attributeDetails)}}}return matchingElements}function processCycle(element,classList){var cycleChildren=element.children,cycleListString=element.getAttribute(classList),cycleListArray=cycleListString.split(" "),listLength=cycleListArray.length;for(var i=0;i<cycleChildren.length;i++){var modulo=i%listLength,childElement=cycleChildren[i],hasClasses=childElement.className.length,spacer,newClass;spacer=(hasClasses)?" ":"";newClass=cycleListArray[modulo];childElement.className+=spacer+newClass}}function classCycle(){var classCycleElements=getElementsWithAtt("data-cycle");if(classCycleElements){for(var i=0;i<classCycleElements.length;i++){var subArray=classCycleElements[i];processCycle(subArray[0],subArray[1])}}}