function getElementsWithAtt(attribute){var matchingElements=[],allElements=document.getElementsByTagName("*");for(var i=0;i<allElements.length;i++){var hasAttribute=allElements[i].getAttribute(attribute);if(hasAttribute){matchingElements.push(allElements[i])}}return matchingElements}function processCycle(element){var cycleChildren=element.children,cycleListString=element.getAttribute("data-cycle-list"),cycleListArray=cycleListString.split(" "),listLength=cycleListArray.length;for(var i=0;i<cycleChildren.length;i++){var modulo=i%listLength,newClass=" "+cycleListArray[modulo];cycleChildren[i].className+=newClass}}var classCycleElements=getElementsWithAtt("data-cycle-list");if(classCycleElements){for(var i=0;i<classCycleElements.length;i++){processCycle(classCycleElements[i])}}